package com.github.nadyaalimin.randomgeneratorimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleclass MainActivity : AppCompatActivity() {    lateinit var resultText: TextView    lateinit var rollButton: Button    lateinit var viewCoin: ImageView    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        resultText = findViewById(R.id.result_text) as TextView        rollButton = findViewById(R.id.roll_button) as Button        viewCoin = findViewById(R.id.view_coin) as ImageView        rollButton.setOnClickListener {            flipCoin()            Toast.makeText(this, "Button Clicked", Toast.LENGTH_SHORT).show()        }    }    private fun flipCoin() {        val randomint = Random().nextInt(2)        if (randomint != 0)        {            resultText.text="Tail"            viewCoin.setImageResource(R.drawable.tail)        }        else {            resultText.text = "Head"            viewCoin.setImageResource(R.drawable.head)        }    }}